import React from 'react'
import Header from '../components/Header'
import Footer from '../components/Footer'


const createItem = () => {
  let addItemBtn = document.getElementById("addItemBtn");

  // Lấy ra tham chiếu đến danh sách mục (ul)
  let itemList = document.getElementById("itemList");

  // Số lượng item đã được tạo
  let itemCount = 0;

  // Xử lý sự kiện click vào nút "Thêm Item"
  addItemBtn.addEventListener("click", function() {
      // Tạo một phần tử li mới
      let newItem = document.createElement("li");

      // Gán nội dung cho phần tử li mới
      itemCount++;
      newItem.textContent = "Item " + itemCount;

      itemList.appendChild(newItem);
    });
}

const Pages = () => {
  return (
    <div className={'container'}>
      <Header />
        <div className={'main'}>
          <div className={'mainTitle'}>
            <h4>Content Section</h4>
          </div>
          <div className={'mainItem'}>
            <div className={'content'}>
              <p>This is content of the app. It can include various elements and components</p>
            </div>
            <div>
              <button onClick={createItem()}>Click Me</button>
              <ul id={'listItem'}>
              </ul>
            </div>
          </div>
        </div>
      <Footer />
    </div>
  )
}

export default Pages
