import React from 'react'
import Header from '../components/Header'
import Footer from '../components/Footer'



const Pages = () => {
  const [items, setItems] = useState([]);

  // Hàm xử lý sự kiện khi người dùng nhấn vào nút "Thêm Item"
  const handleAddItem = () => {
    // Tạo một item mới với nội dung "Item" kèm số thứ tự của item
    const newItem = `Item ${items.length + 1}`;

    // Thêm item mới vào danh sách các item bằng cách cập nhật state
    setItems([...items, newItem]);
  };
    return (
      <div className={'container'}>
        <Header />
        <div className={'main'}>
          <div className={'mainTitle'}>
            <h4>Content Section</h4>
          </div>
          <div className={'mainItem'}>
            <div className={'content'}>
              <p>This is content of the app. It can include various elements and components</p>
            </div>
            <div>
              <button onClick={createItem()}>Click Me</button>
              <ul>
                {items.map((item, index) => (
                  <li key={index}>{item}</li>
                ))}
              </ul>
            </div>
          </div>
        </div>
        <Footer />
      </div>
    )
  }

  export default Pages
